import React, { createContext } from "react";
import { StyleSheet } from "react-native";
import { View } from "react-native";
import { Button } from "react-native";
import { useContext } from 'react';
import { TextInput } from 'react-native-gesture-handler';
import { useState } from 'react';
import { theme } from "../../components/theme";
import { SafeAreaView } from "react-native-safe-area-context";

const SubjectContext = createContext({
    number: 6,
});



const FirstSetting = ({navigation}) => {
    const [count, setCount] = useState(0);

    return (
    <SafeAreaView style = {startviewStyles.container}>
        <Text style={startviewStyles.startTitle}>Lectures: {count} </Text>
        <Pressable style = {FSstyles.button}onPress={() => {setCount(count+1);}}>
            <Text style={FSstyles.text}>+</Text>
        </Pressable>
        <Pressable style = {FSstyles.button}onPress={() => {setCount(count-1);}}>
            <Text style={FSstyles.text}>-</Text>
        </Pressable>
    </SafeAreaView>
    )
}


const FSstyles = StyleSheet.create({
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 24,
        borderRadius: 0,
        elevation: 3,
        backgroundColor: theme.color1,
    },
    text: {
        fontsize: 40,
        lineHeight: 14,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: theme.color1,
    },
    view: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: theme.dark,
    }
});

export default FirstSetting;
