const ReviewPage = ({route, navigation}) => {
  const {itemId} = route.params;

   return (
      <View style={{ flex: 1, alignItems:'center',justifyContent:'center'}}>
       <FlatList
         data={Object.values(itemId)}
         keyExtractor={(item)=>item.id}
         renderItem={({item})=>(
           item.completed ? (
           <Text style={viewStyles.doneListText}>
             {item.text}
           </Text>
           ):(
           null
         ))
           }
         />         
      </View>
  );
}
const SearchPage = () =>{
   return(
      <View>
      <Text>Search</Text>
      </View>
   );
}

const CalanderPage = () =>{
   return(
      <Calendars />
   )
}

const Main = () => {
   return(
          <NavigationContainer independent = {true}>
            <Tab.Navigator
              initialRouteName="Home"
              screenOptions={{
                tabBarActiveTintColor: '#224E41',
                tabBarShowLabel: false,
              }}>
            <Tab.Screen
                name="Home"
                component={HomeScreen}
                options={{
                  title: 'Home',
                  tabBarIcon: ({color, size}) => (
                    <Icon name="home" color={color} size={size} />
                  ),
                }}
              />
            <Tab.Screen
                name="Calendar"
                component={CalanderPage}
                options={{
                  title: 'Calendar',
                  tabBarIcon: ({color, size}) => (
                    <Icon name="calendar-today" color={color} size={size} />
                  ),
                }}
              />
              <Tab.Screen
                name="AddSub"
                component={InputSubject}
                options={{
                  title: 'Add Subject',
                  tabBarIcon: ({color, size}) => (
                    <Icon name="subject" color={color} size={size} />
                  ),
                }}
              />
            <Tab.Screen
                name="Search"
                component={SearchScreen}
                options={{
                  title: 'Search',
                  tabBarIcon: ({color, size}) => (
                    <Icon name="search" color={color} size={size} />
                  ),
                }}
              />
              <Tab.Screen
                name="Review"
                component={ReviewPage}
                options={{
                  title: 'Review',
                  tabBarIcon: ({color, size}) => (
                    <Icon name="message" color={color} size={size} />
                  ),
                }}
              />
            </Tab.Navigator>
          </NavigationContainer>


   )
  };

export default Main;
